mapper.select:
select c.*
from(
	select rownum as rnum, b.*
	from(
		select * from board_free
		
		order by free_no desc
	)b
)c

where c.rnum>#{firstRowNum}
and c.rnum<=#{pageSize}+#{firstRowNum}

mapper.update:
update board_free
set free_title=#{freeTitle}, free_contents=#{freeContents}

	,free_name=#{freeName}

where free_no=#{freeNo}
